m_off_t MegaApiImpl::sizeDifference(Node *i, Node *j)
{
    m_off_t iSize = 0;
    if (i->type == FILENODE)
    {
        iSize = i->size;
    }
    else
    {
        iSize = i->getCounter().storage + i->getCounter().versionStorage;
    }

    m_off_t jSize = 0;
    if (i->type == FILENODE)
    {
        jSize = j->size;
    }
    else
    {
        jSize = j->getCounter().storage + j->getCounter().versionStorage;
    }

    return iSize - jSize;
}

